import { useState } from 'react';

function AddServiceForm(props) {
  const [serviceType, setServiceType] = useState('parking');
  const [serviceName, setServiceName] = useState('');
  const [serviceLocation, setServiceLocation] = useState('');
  const [servicePrice, setServicePrice] = useState('');
  const [serviceAvailability, setServiceAvailability] = useState('');
  const [serviceDescription, setServiceDescription] = useState('');
  const [serviceImage, setServiceImage] = useState(null);
  const [serviceSlots, setServiceSlots] = useState('');

  function handleSubmit(event) {
    event.preventDefault();
    const formData = new FormData();
    formData.append('service_type', serviceType);
    formData.append('service_name', serviceName);
    formData.append('service_location', serviceLocation);
    formData.append('service_price', servicePrice);
    formData.append('service_availability', serviceAvailability);
    formData.append('service_description', serviceDescription);
    formData.append('service_image', serviceImage);
    formData.append('service_slots', serviceSlots);
    props.onSubmit(formData);
  }

  return (
    <form onSubmit={handleSubmit}>
      <label htmlFor="service_type">Service Type:</label>
      <select id="service_type" value={serviceType} onChange={(event) => setServiceType(event.target.value)}>
        <option value="parking">Parking</option>
        {/* Add more service types here */}
      </select>
      <br/>

      <label htmlFor="service_name">Name:</label>
      <input type="text" id="service_name" value={serviceName} onChange={(event) => setServiceName(event.target.value)} required/>
      <br/>

      <label htmlFor="service_location">Location:</label>
      <input type="text" id="service_location" value={serviceLocation} onChange={(event) => setServiceLocation(event.target.value)} required/>
      <br/>

      <label htmlFor="service_price">Price:</label>
      <input type="number" id="service_price" value={servicePrice} onChange={(event) => setServicePrice(event.target.value)} min="0" required/>
      <br/>

      <label htmlFor="service_availability">Availability:</label>
      <input type="text" id="service_availability" value={serviceAvailability} onChange={(event) => setServiceAvailability(event.target.value)} required/>
      <br/>

      <label htmlFor="service_description">Description:</label>
      <textarea id="service_description" value={serviceDescription} onChange={(event) => setServiceDescription(event.target.value)}></textarea>
      <br/>

      <label htmlFor="service_image">Image:</label>
      <input type="file" id="service_image" onChange={(event) => setServiceImage(event.target.files[0])}/>
      <br/>

      <label htmlFor="service_slots">Slots:</label>
      <input type="number" id="service_slots" value={serviceSlots} onChange={(event) => setServiceSlots(event.target.value)} min="0" required/>
      <br/>

      <input type="submit" value="Add Service"/>
    </form>
  );
}

export default AddServiceForm;
